import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import styles from './Authentification.module.css';
import { useAuth } from './AuthContext'; // Подставьте правильный путь к вашему AuthContext.js

export default function RegistrationForm() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const { login } = useAuth(); // Получаем функцию login из контекста авторизации
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      // Предполагается, что сервер также возвращает id пользователя
      // Этот ID должен быть сохранен в localStorage при успешной аутентификации
      // и передан в функцию login
      login(token, localStorage.getItem('userId'));
    }
  }, [login]);

  const handleInputChange = (e, setValue) => {
    setValue(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const formData = new FormData();
      formData.append('username', username);
      formData.append('password', password);

      const response = await axios.post('http://127.0.0.1:8000/auth/login', formData, {
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
        },
        withCredentials: true,
      });

      navigate('/master-group');
      console.log('Успешный вход:', response.data);
      login(response.data.token, response.data.id); // Передача данных аутентификации в контекст
      localStorage.setItem('token', response.data.token);
      localStorage.setItem('userId', response.data.id); // Сохранение ID пользователя
    } catch (error) {
      console.error('Ошибка входа:', error.response ? error.response.data : 'Сервер не ответил');
    }

    setUsername('');
    setPassword('');
  };

  return (
    <div className={styles.registrationContainer}>
      <div className={styles.centeredContent}>
        <h1 className={styles.registrationTitle}>Вход в GOoger</h1>
        <form className={styles.registrationForm} onSubmit={handleSubmit}>
          <br />
          <label className={styles.labelText}>
            Адрес электронной почты
            <input
              type="text"
              value={username}
              onChange={(e) => handleInputChange(e, setUsername)}
              className={styles.inputField}
            />
          </label>
          <br />
          <label className={styles.labelText}>
            Пароль
            <input
              type="password"
              value={password}
              onChange={(e) => handleInputChange(e, setPassword)}
              className={styles.inputField}
            />
          </label>
          <br />
          <button type="submit" className={styles.submitButton}>
            Войти
          </button>
        </form>
      </div>
    </div>
  );
}
