import React, { useEffect, useState } from 'react';
import { useAuth } from '../Auth/AuthContext';
import axios from 'axios';

const Chat = () => {
  const [ws, setWs] = useState(null);
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');

  const { token } = useAuth();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const userIdResponse = await axios.get('http://127.0.0.1:8000/get_current_user', {
          withCredentials: true,
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        const userId = userIdResponse.data.user_id;

        if (!userId) {
          console.error('User ID is not available.');
          return;
        }

        const newWs = new WebSocket(`ws://localhost:8000/chat/ws/${userId}`);
        setWs(newWs);

        newWs.onopen = () => {
          console.log('Connected to the WebSocket');
        };

        newWs.onmessage = (event) => {
          const message = event.data;
          setMessages((prevMessages) => [...prevMessages, message]);
        };

        newWs.onclose = (event) => {
          console.error('WebSocket closed:', event);
          // Try to reconnect after a delay
          setTimeout(() => {
            console.log('Reconnecting to WebSocket...');
            const reconnectWs = new WebSocket(`ws://localhost:8000/chat/ws/${userId}`);
            setWs(reconnectWs);
          }, 1000);
        };

        newWs.onerror = (error) => {
          console.error('WebSocket error:', error);
        };
      } catch (error) {
        console.error('Error fetching user ID:', error);
      }
    };

    fetchData();

    return () => {
      if (ws) {
        ws.close();
      }
    };
  }, [token]);

  const sendMessage = () => {
    if (inputMessage && ws) {
      ws.send(inputMessage);
      setInputMessage('');
    }
  };

  return (
    <div>
      <div>
        {messages.map((message, index) => (
          <div key={index}>{message}</div>
        ))}
      </div>
      <div>
        <input
          type="text"
          value={inputMessage}
          onChange={(e) => setInputMessage(e.target.value)}
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
};

export default Chat;
