// Ваш файл Chat.js

import React, { useEffect, useState } from 'react';

const Chat = () => {
  const [messages, setMessages] = useState([]);
  const [socket, setSocket] = useState(null);
  const [inputValue, setInputValue] = useState('');
  const [userId, setUserId] = useState(null);

  useEffect(() => {
    // Вместо запроса на сервер о userId, предполагаем, что userId уже доступен после успешной аутентификации.
    // Замените эту логику в соответствии с вашим механизмом аутентификации.
    // const fetchUserId = async () => {
    //   // Ваш запрос на сервер для получения userId
    // };
    // fetchUserId();

    // Предположим, что у вас есть механизм, который предоставляет userId после успешной аутентификации.
    // Просто замените этот механизм вместо вызова fetchUserId().
    const userIdFromAuth = getUserIdFromAuth(); // Функция, возвращающая userId
    setUserId(userIdFromAuth);
  }, []); // Выполняется один раз при монтировании компонента

  useEffect(() => {
    if (userId) {
      const newSocket = new WebSocket(`ws://localhost:8000/ws/${userId}`);

      newSocket.onopen = () => {
        console.log('WebSocket connection opened');
      };

      newSocket.onmessage = (event) => {
        const message = event.data;
        setMessages((prevMessages) => [...prevMessages, message]);
      };

      newSocket.onclose = () => {
        console.log('WebSocket connection closed');
      };

      setSocket(newSocket);

      return () => {
        newSocket.close();
      };
    }
  }, [userId]);

  const sendMessage = () => {
    if (socket && inputValue.trim() !== '') {
      socket.send(inputValue);
      setInputValue('');
    }
  };

  return (
    <div>
      <div>
        {messages.map((msg, index) => (
          <div key={index}>{msg}</div>
        ))}
      </div>
      <input
        type="text"
        placeholder="Type your message"
        value={inputValue}
        onChange={(e) => setInputValue(e.target.value)}
        onKeyDown={(e) => {
          if (e.key === 'Enter') {
            sendMessage();
          }
        }}
      />
      <button onClick={sendMessage}>Send</button>
    </div>
  );
};

export default Chat;
